import client from '../../client'

const Project = (props) => {
  const { title = 'Missing title', atelierName = 'Atelier fehlt' } = props

  return (
    <article>
      <h2>Titel: {title}</h2>
      <p>Das Projekt <span>{title}/</span> ist Teil des Atelier <span>{atelier}</span></p>
    </article>
  )
}

Project.getInitialProps = async function(context) {
    // It's important to default the slug so that it doesn't return "undefined"
    const { slug = "" } = context.query
    return await client.fetch(`
      *[_type == "projekt" && slug.current == $slug][0]{title, "atelierName": atelier->title}
    `, { slug })
  }

export default Project
