import client from '../../client'

const Project = (props) => {
  const { title = 'Missing title', atelier = 'Atelier fehlt' } = props

  return (
    <article>
      <h2>{title}</h2>
      <p>Das Projekt {title} ist Teil des Atelier {atelier}</p>
    </article>
  )
}

Project.getInitialProps = async function(context) {
    // It's important to default the slug so that it doesn't return "undefined"
    const { slug = "" } = context.query
    return await client.fetch(`
      *[_type == "projekt" && slug.current == $slug][0]{title, "atelier": atelier->title}
    `, { slug })
  }

export default Project
